    override func viewDidLoad() {
        super.viewDidLoad()

        self.view.backgroundColor = .white
        self.view.addSubview(termsLabel)
        self.view.addSubview(termsTextView)
    
        NSLayoutConstraint.activate([
            termsLabel.centerXAnchor.constraint(equalTo: self.view.safeAreaLayoutGuide.centerXAnchor),
            termsLabel.topAnchor.constraint(equalTo: self.view.topAnchor,constant: 20)
        ])
        NSLayoutConstraint.activate([
            termsTextView.topAnchor.constraint(equalTo: self.termsLabel.bottomAnchor,constant: 20),
            termsTextView.trailingAnchor.constraint(equalTo: self.view.trailingAnchor, constant: -16),
            termsTextView.leadingAnchor.constraint(equalTo: self.view.leadingAnchor,constant: 16),
            termsTextView.bottomAnchor.constraint(equalTo: self.view.bottomAnchor, constant: UIScreen.main.bounds.height/2)
        ])
        
    }
    
//sayfa full ama yarıya kadar açılıyor öyle düşün.
    override func viewDidLayoutSubviews() {
        super.viewDidLayoutSubviews()
        self.view.frame = CGRect(x: 0, y: UIScreen.main.bounds.height / 5 * 2, width: self.view.bounds.width, height: UIScreen.main.bounds.height / 5 * 3)
        self.view.layer.cornerRadius = 20
        self.view.layer.masksToBounds = true
    }
}
